var axios = require('axios');

id_user = '5ad5365d0b212afd7d8b458c';

var data = {
  api_key : process.env.PAYBOOK_KEY,
	id_user : id_user,
	password : "12345678a",
	taxpayer : "AAA010101AAA",
	cer: "",
	key : ""
};

function createTaxpayer(){
  axios.post(process.env.PAYBOOK_URL + 'invoicing/mx/taxpayers', data, {
    headers: {
      'Content-Type' : 'application/json'
    },
  })
  .then(function(response){
    console.log("Taxpayer creado");
    console.log(response.data);
  })
  .catch(function(error){
    console.log(error);
  });
}

axios.get(process.env.PAYBOOK_URL + 'invoicing/mx/taxpayers?api_key=' + process.env.PAYBOOK_KEY + '&id_user=' + id_user)
  .then(function(response){
    if(response.data['response'] == '')
      createTaxpayer();
    else
      console.log(response.data);
  })
  .catch(function(error){
    console.log(error);
  });
